import { useState } from "react"


export function Account(props) {

    const [account, setState] = useState({
        accountHolder: "",
        accountId: null,
        experience: null,
        salary: null
    })


    const addNewAccount = (ev) => {
        // take action to make http request or something 
        ev.preventDefault()
        //   setAccount({accId: account.accId, accHolder: ev.target.value.name})
    }


    const updateAccount = (key, value) => {
        // using ternary operator in state 
        let acc = { ...state }
        acc[key] = value
        setState(acc)
    }

    return (
        <div>
            <form onSubmit={e => addNewAccount(e)}>
                <label>
                    Account Holder : <input type="text" name="accHolder" value={state.accountHolder} onChange={e => updateAccount('accountHolder', e.target.value)}></input>
                </label> 
                <label>
                    Account Id: <input type="number" name="accId" value={state.accountId} onChange={e => updateAccount('accountId', e.target.value)}></input>
                </label>
                <label>
                    Salary: <input type="number" name="salary" value={state.salary} onChange={e => updateAccount('salary', e.target.value)}></input>
                </label>
                <label>
                   Experience: <input type="number" name="experience" value={state.experience} onChange={e => updateAccount('experience', e.target.value)}></input>
                </label>
                <input type="submit" value="submit" />

            </form >
        </div>
    )
}
