import { useState } from "react"
import {useDispatch} from 'react-redux'
import { createAccount } from "../Actions/AccountActions";
import {} from 'semantic-ui-react'

export function Account(props) {

    const dispatch = useDispatch();
    const [account, setAccount] = useState({
        accountHolder: "",
        accountId: null,
        experience: null,
        salary: null
    })


    const addNewAccount = (event) => {
        // take action to make http request or something 

        dispatch(createAccount(account))
        event.preventDefault()
        //   setAccount({accId: account.accId, accHolder: ev.target.value.name})
    }


    const updateAccount = (key, value) => {
        // using ternary operator in state 
        let acc = { ...account }
        acc[key] = value
        setAccount(acc)
    }

    return (
        <div>
            <form onSubmit={e => addNewAccount(e)}>
                <label>
                    Account Holder : <input type="text" name="accHolder" value={account.accountHolder} onChange={e => updateAccount('accountHolder', e.target.value)}></input>
                </label> 
                <label>
                    Account Id: <input type="number" name="accId" value={account.accountId} onChange={e => updateAccount('accountId', e.target.value)}></input>
                </label>
                <label>
                    Salary: <input type="number" name="salary" value={account.salary} onChange={e => updateAccount('salary', e.target.value)}></input>
                </label>
                <label>
                   Experience: <input type="number" name="experience" account={account.experience} onChange={e => updateAccount('experience', e.target.value)}></input>
                </label>
                <input type="submit" value="submit" />

            </form >
        </div>
    )
}
