import { useState } from "react"


export function Account(props) {

    const [account, setAccount] = useState({
        accId: 101,
        accHolder: "AKG"
    })

   const addNewAccount = (ev) => {
       console.log(ev.target.value)
      setAccount({accId: account.accId, accHolder: ev.target.value.name})
   }

    return (
        <div>
           <div>{ account.accHolder}</div>
           <div>{ account.accId}</div>
            <form>
                <label>
                    Name: <input type="text" name="accHolder"></input>
                </label>
                <label>
                    Name: <input type="text" name="accid"></input>
                </label>
            <input type="submit" value="submit" onClick={addNewAccount}/>

            </form>
        </div>
    )
}
