import {all, fork} from 'redux-saga/effects'
import { watchDeleteAccount, watchGetAccounts } from './accountSagas'


export default function* rootSaga() {

    yield all([
        fork(watchGetAccounts),
        fork(watchDeleteAccount),
        fork(watch)
    ]);
}
