import DataTable from 'react-data-table-component';
import { React, useState, useEffect } from 'react'
import {useDispatch} from 'react-redux'
import { deleteAccount } from '../Actions/AccountActions';
import {useSelector} from 'react-redux'

export function Accounts(props) {


    const loading = useSelector((state) => state.accounts.loading)

    const dispatch = useDispatch()
    // single click event use later for overlay. 
    const handleRowClicked = (row) => {
        console.log("ROW CLICKED")
    }

    // double click user later for edit 
    const handleRowDoubleClicked = (row) => console.log("ROW Double CLICKED" + row)


   const handleEditClick = (e) => {
    console.log("BUTTON " + JSON.stringify(e))
   }

   const handleDeleteClick = (row) => {
    dispatch(deleteAccount(row.accountId))

   }

    const [columns, setColumns] = useState([]);

    useEffect(() => {
        const timeout = setTimeout(() => {
            setColumns([
                {
                    name: 'ACCOUNT ID',
                    selector: row => row.accountId,
                    sortable: true,
                    // sortFunction: caseInsensitiveSort
                },
                {
                    name: 'ACCOUNT HOLDER',
                    selector: row => row.accountHolder,
                },
                {
                    name: 'SALARY',
                    selector: row => row.salary,
                    sortable: true,
                    // sortFunction: caseInsensitiveSort
                },
                {
                    name: 'EXPERIENCE',
                    selector: row => row.experience,
                    sortable: true,
                    // sortFunction: caseInsensitiveSort
                },
                {
                    cell: (row) => <button onClick={() => handleEditClick (row)}> EDIT</button>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                },
                {
                    cell: (row) => <button onClick={() => handleDeleteClick (row)}> DELETE</button>,
                    ignoreRowClick: true,
                    allowOverflow: true,
                    button: true,
                }
            ])
        }, 10);
        return () => clearTimeout(timeout);
    }, []);

    return <DataTable columns={columns}
        data={props.accounts}
        progressPending={loading}
        pagination
        onRowClicked={(e) => handleRowClicked(e)}
        onRowDoubleClicked={(e) => handleRowDoubleClicked(e)}
    />;
};
