import { GET_ACCOUNTS } from "../Actions/AccountActions";

const intialState = {
  accounts: []
}

export function accountReducer(previousState = intialState, action) {
  switch (action.type) {
    case SET_ACCOUNTS:
      return { ...previousState, accounts: action.payload }

    default:
      return previousState;
  }
}
