import { useState } from "react"


export function Account(props) {

    const [state, setState] = useState({
        accountHolder: "",
        accountId: null,
        experience: null,
        salary: null
    })


    const addNewAccount = (ev) => {
        // take action to make http request or something 
        ev.preventDefault()
        //   setAccount({accId: account.accId, accHolder: ev.target.value.name})
    }


    const updateAccount = (key, value) => {
        // using ternary operator in state 
        let acc = {...state}
        acc[key] = value
        setState(acc)
    }

    return (
        <div>
            <div>{state.accHolder}</div>
            <div>{state.accId}</div>
            <form onSubmit={e => addNewAccount(e)}>
                <label>
                    Account Holder : <input type="text" name="accHolder" value={state.accHolder} onChange={e => updateAccount('accHolder', e.target.value)}></input>
                </label>
                <label>
                    Account Id: <input type="number" name="accId" value={state.accId} onChange={e => updateAccount('accId', e.target.value)}></input>
                </label>
                <input type="submit" value="submit" />

            </form >
        </div>
    )
}
